import 'package:cloud_firestore/cloud_firestore.dart';import 'package:loja_virtual/datas/product_data.dart';class CartProduct {  String cardId;  String category;  String productId;  int quantity;  String color;  ProductData productData;  CartProduct.fromDocument(DocumentSnapshot document) {    cardId = document.documentID;    category = document.data["category"];    productId = document.data["productId"];    quantity = document.data["quantity"];    color = document.data["color"];  }  Map<String, dynamic> toMap() {    return {      "category": category,      "productId": productId,      "quantity": quantity,      "color": color,      "product": productData.toResumedMap()    };  }}